mapper.select:
SELECT * FROM (SELECT ROWNUM AS IDX, UI.* FROM (SELECT * FROM UNIFORM_INFO
UNIFORM_NAME LIKE '%'||#{uniformName}||'%' COLOR LIKE '%'||#{color}||'%'
ORDER BY UNI_INFO_NO) UI ) UI WHERE IDX BETWEEN #{beginList} AND #{endList}

mapper.update:
UPDATE UNIFORM_INFO
UNIFORM_NAME = #{uniformName}, COLOR = #{color}, PRICE = #{price}, S_COUNT = #{sCount}, M_COUNT = #{mCount}, L_COUNT = #{lCount}, XL_COUNT = #{xlCount}
WHERE UNI_INFO_NO = #{uniInfoNo}

UPDATE UNIFORM_INFO
SET UNIFORM_IMG = #{uniformImg}
WHERE UNI_INFO_NO = #{uniInfoNo}
